	SELECT
		'f55eddce-b55d-4f9e-8ed9-3de715b1fe7a',
		'Active Malaria',
		'ACTIVE_MALARIA',
		'1',
		'ACTIVE',
		97104,
		now(),
		97104,
		now(),
