{"value":"handleDiseasesChange","code":"(function handleDiseasesChange() {\n\tif ($formData$.childServiceData?.diseases?.length > 0\n        && $formData$.childServiceData?.diseases?.includes(''NONE'')) {\n        $formData$.childServiceData.diseases = [''NONE''];\n    }\n});"},{"value":"setDropdownValues","code":"(function setDropdownValues() {\n\tif ($formData$.childServiceData?.referral_for) {\n        $formData$.childServiceData.referral_for === -1 && ($formData$.childServiceData.referral_for = ''OTHER'');\n\t}\n\t\n\t$formData$.childServiceData.symptoms = $formData$.childServiceData?.symptoms?.split('','').map(data => {\n       if (![''OTHER''].includes(data)) {\n           return parseInt(data);\n       } else {\n           return data;\n       }\n    });\n    \n    $formData$.childServiceData.diseases = $formData$.childServiceData?.diseases?.split('','').map(data => {\n       if (![''OTHER''].includes(data)) {\n           return parseInt(data);\n       } else {\n           return data;\n       }\n    });\n    \n    $formData$.childServiceData.any_disability = $formData$.childServiceData?.any_disability?.split('','').map(data => {\n       if (![''OTHER''].includes(data)) {\n           return parseInt(data);\n       } else {\n           return data;\n       }\n    });\n});"},{"value":"handlePhysicalDisabilityChange","code":"(function handlePhysicalDisabilityChange() {\n    if ($formData$.childServiceData?.any_disability?.length > 0\n        && $formData$.childServiceData?.any_disability?.includes(''NONE'')) {\n        $formData$.childServiceData.any_disability = [''NONE''];\n    }\t\n});"},{"value":"findMalnutritionGrade","code":"(function findMalnutritionGrade() {\n\tlet malnutritionGrade = \"Red\";\n    let weight = $formData$.childServiceData.weight\n\n    let lowerBoundaryOfWeight = 0;\n    let midBoundaryOfWeight = 0;\n    let upperBoundaryOfWeight = 0;\n    let higherUpperBoundaryOfWeight = 0;\n\n    if ($infoData$.childDetails.gender === ''Male'') {\n        let boundaryString = $utilityData$.gradeMapForBoys[$utilityData$.ageInMonths];\n        if (boundaryString) {\n            lowerBoundaryOfWeight = parseFloat(boundaryString[0]);\n            midBoundaryOfWeight = parseFloat(boundaryString[1]);\n            upperBoundaryOfWeight = parseFloat(boundaryString[2]);\n            higherUpperBoundaryOfWeight = parseFloat(boundaryString[3]);\n        }\n    } else {\n        let boundaryString = $utilityData$.gradeMapForGirls[$utilityData$.ageInMonths];\n        if (boundaryString) {\n            lowerBoundaryOfWeight = parseFloat(boundaryString[0]);\n            midBoundaryOfWeight = parseFloat(boundaryString[1]);\n            upperBoundaryOfWeight = parseFloat(boundaryString[2]);\n            higherUpperBoundaryOfWeight = parseFloat(boundaryString[3]);\n        }\n    }\n\n    if (weight <= lowerBoundaryOfWeight) {\n        malnutritionGrade = \"Severely Malnourished\";\n    } else if (weight > lowerBoundaryOfWeight && weight <= midBoundaryOfWeight) {\n        malnutritionGrade = \"Malnourished\";\n    } else if (weight > midBoundaryOfWeight && weight <= upperBoundaryOfWeight) {\n        malnutritionGrade = \"Ideal\";\n    } else if (weight > upperBoundaryOfWeight && weight <= higherUpperBoundaryOfWeight) {\n        malnutritionGrade = \"Obese\";\n    } else if (weight > higherUpperBoundaryOfWeight) {\n        malnutritionGrade = \"Severely Obese\";\n    }\n\n    $formData$.childServiceData.malnutrition_grade = malnutritionGrade;\n    console.log($formData$.childServiceData.malnutrition_grade)\n});"}],"formQueries":[{"value":"Get Child Details","queryCode":"get_child_details","params":"member_id","paramConfig":"{\"member_id\":\"$formData$.childServiceData.member_id\"}","query":"with\n    hof_det as (\n        select\n            im.mobile_number\n        from\n            imt_member im\n            where im.family_id = (select family_id from imt_member where id = #member_id#)\n            and im.family_head\n    )\nselect\n    im.unique_health_id,\n    concat_ws ('' '', im.first_name, im.middle_name, im.last_name) as member_name,\n    cast(age (im.dob) as text) as age,\n    im.dob,\n    case\n        when im.gender = ''F'' then ''Female''\n        else ''Male''\n    end as gender,\n    im.nrc_number,\n    (\n        select\n            value\n        from\n            listvalue_field_value_detail\n        where\n            id = im.marital_status\n    ) as marital_status,\n    (\n        select\n            value\n        from\n            listvalue_field_value_detail\n        where\n            cast(id as text) = case\n                when imf.religion is not null then imf.religion\n                when im.member_religion is not null then im.member_religion\n                else null\n            end\n    ) as religion,\n    case\n        when im.last_method_of_contraception = ''NATURAL_METHODS'' then ''Natural methods''\n        when im.last_method_of_contraception = ''ORALPILLS''\n        or im.last_method_of_contraception = ''ORAL_PILLS'' then ''Oral pills''\n        when im.last_method_of_contraception = ''IUCD5'' then ''IUCD 5 years''\n        when im.last_method_of_contraception = ''IUCD10'' then ''IUCD 10 years''\n        when im.last_method_of_contraception = ''STERILIZATION'' then ''Sterilization''\n        when im.last_method_of_contraception = ''CONDOM'' then ''Condom''\n        when im.last_method_of_contraception = ''MLSTR'' then ''Male sterilization''\n        when im.last_method_of_contraception = ''CONTRA'' then ''Emergency Contraceptive Pills''\n        when im.last_method_of_contraception = ''INJECTABLES'' then ''Injectables''\n        when im.last_method_of_contraception = ''BARRIER_METHOD'' then ''Barrier Method''\n        when im.last_method_of_contraception = ''CHHAYA'' then ''Chhaya''\n        when im.last_method_of_contraception = ''ANTARA'' then ''Antara''\n        else im.last_method_of_contraception\n    end as last_method_of_contraception,\n    im.lmp,\n    cast(im.additional_info as json)->> ''hivTest'' as hiv_status,\n    (\n        select\n            string_agg (value, '', '')\n        from\n            listvalue_field_value_detail lfvd\n        where\n            cast(id as text) in (\n                select\n                    unnest (string_to_array (im.chronic_disease, '',''))\n            )\n    ) as chronic_disease,\n    im.birth_cert_number,\n    im.mother_name,\n    im.birth_weight,\n    im.weight,\n    im.is_pregnant,\n    im.immunisation_given,\n    cast(im.additional_info as json)->> ''developmentalDelays'' as developmental_delay,\n    cast(im.additional_info as json)->> ''lastServiceLongGate'' as last_service_date,\n    (\n        select\n            mobile_number\n        from\n            hof_det\n    ) as family_phone_no,\n    concat_ws ('' '', imf.house_number, imf.address1, imf.address2) as family_address\nfrom\n    imt_member im\n    inner join imt_family imf on imf.family_id = im.family_id   \nwhere im.id = #member_id#;","response":"$infoData$.childDetails","isResultArray":null,"successMessage":null,"failureMessage":"Child Details Not Found","isResponseMandatory":true,"isField":null,"isPagination":false},{"value":"Get Health Infra","queryCode":"get_health_infra","params":"","paramConfig":"{}","query":"select  h.name , h.id,h.type as type from health_infrastructure_details h;\n","response":"$utilityData$.healthInfraList","isResultArray":true,"successMessage":null,"failureMessage":null,"isResponseMandatory":false,"isField":true,"isPagination":false,"dropdownKey":"id","dropdownValue":"name","toVariable":true},{"value":"Get Child Service Data","queryCode":"get_child_service_data","params":"child_service_id","paramConfig":"{\"child_service_id\":\"$stateParams$.id\"}","query":"select\n    rcsm.id,\n    rcsm.member_id,\n    rcsm.family_id,\n    rcsm.location_id,\n    rcsm.location_hierarchy_id,\n    rcsm.weight,\n    rcsm.ifa_syrup_given,\n    rcsm.complementary_feeding_started,\n    rcsm.is_tr