      ELSE ''OTHER''\n            END\n        ELSE ''''\n    END AS symptoms\nfrom\n    rch_child_service_master rcsm\n    left join rch_child_service_diseases_rel rcsd on rcsm.id = rcsd.child_service_id\n    left join rch_child_service_symptoms_rel rcss on rcsm.id = rcss.child_service_id\nwhere\n    id = #child_service_id#\ngroup by\n    rcsm.id\norder by\n    modified_on desc\nlimit\n    1;","response":"$formData$.childServiceData","isResultArray":null,"successMessage":null,"failureMessage":"Screening Records Not Found","isResponseMandatory":true,"isField":null,"isPagination":false},{"value":"Get Child Immunisations","queryCode":"get_child_immunisations","params":"member_id","paramConfig":"{\"member_id\":\"$formData$.childServiceData.member_id\"}","query":"select immunisation_given, given_on from rch_immunisation_master where member_id = #member_id#","response":"$infoData$.immunisationGiven","isResultArray":null,"successMessage":null,"failureMessage":null,"isResponseMandatory":null,"isField":null,"isPagination":false},{"value":"Retrieve Hospitals by Institution Type","queryCode":"retrieve_hospitals_by_infra_type","params":"infraType","paramConfig":"{\"infraType\":\"$formData$.childServiceData.death_infra_type\"}","query":"select * from health_infrastructure_details where type = #infraType#","response":null,"isResultArray":true,"successMessage":null,"failureMessage":null,"isResponseMandatory":null,"isField":true,"isPagination":false,"dropdownKey":"id","dropdownValue":"name_in_english"},{"value":"Get User Health Infra","queryCode":"get_user_health_infra","params":"userID","paramConfig":"{\"userID\":\"$loggedInUserId$\"}","query":"select health_infrastrucutre_id from user_health_infrastructure where user_id = #userID# and is_default = true;","response":"$utilityData$.userHealthInfra","isResultArray":null,"successMessage":null,"failureMessage":null,"isResponseMandatory":null,"isField":null,"isPagination":false}]}',
