     let boundaryString = $utilityData$.gradeMapForBoys[$utilityData$.ageInMonths];\n        if (boundaryString) {\n            lowerBoundaryOfWeight = parseFloat(boundaryString[0]);\n            midBoundaryOfWeight = parseFloat(boundaryString[1]);\n            upperBoundaryOfWeight = parseFloat(boundaryString[2]);\n            higherUpperBoundaryOfWeight = parseFloat(boundaryString[3]);\n        }\n    } else {\n        let boundaryString = $utilityData$.gradeMapForGirls[$utilityData$.ageInMonths];\n        if (boundaryString) {\n            lowerBoundaryOfWeight = parseFloat(boundaryString[0]);\n            midBoundaryOfWeight = parseFloat(boundaryString[1]);\n            upperBoundaryOfWeight = parseFloat(boundaryString[2]);\n            higherUpperBoundaryOfWeight = parseFloat(boundaryString[3]);\n        }\n    }\n\n    if (weight <= lowerBoundaryOfWeight) {\n        malnutritionGrade = \"Severely Malnourished\";\n    } else if (weight > lowerBoundaryOfWeight && weight <= midBoundaryOfWeight) {\n        malnutritionGrade = \"Malnourished\";\n    } else if (weight > midBoundaryOfWeight && weight <= upperBoundaryOfWeight) {\n        malnutritionGrade = \"Ideal\";\n    } else if (weight > upperBoundaryOfWeight && weight <= higherUpperBoundaryOfWeight) {\n        malnutritionGrade = \"Obese\";\n    } else if (weight > higherUpperBoundaryOfWeight) {\n        malnutritionGrade = \"Severely Obese\";\n    }\n\n    $formData$.childServiceData.malnutrition_grade = malnutritionGrade;\n    console.log($formData$.childServiceData.malnutrition_grade)\n});"}],"formQueries":[{"value":"Get Child Details","queryCode":"get_child_details","params":"member_id","paramConfig":"{\"member_id\":\"$formData$.childServiceData.member_id\"}","query":"with\n    hof_det as (\n        select\n            im.mobile_number\n        from\n            imt_member im\n            where im.family_id = (select family_id from imt_member where id = #member_id#)\n            and im.family_head\n    )\nselect\n    im.unique_health_id,\n    concat_ws ('' '', im.first_name, im.middle_name, im.last_name) as member_name,\n    cast(age (im.dob) as text) as age,\n    im.dob,\n    case\n        when im.gender = ''F'' then ''Female''\n        else ''Male''\n    end as gender,\n    im.nrc_number,\n    (\n        select\n            value\n        from\n            listvalue_field_value_detail\n        where\n            id = im.marital_status\n    ) as marital_status,\n    (\n        select\n            value\n        from\n            listvalue_field_value_detail\n        where\n            cast(id as text) = case\n                when imf.religion is not null then imf.religion\n                when im.member_religion is not null then im.member_religion\n                else null\n            end\n    ) as religion,\n    case\n        when im.last_method_of_contraception = ''NATURAL_METHODS'' then ''Natural methods''\n        when im.last_method_of_contraception = ''ORALPILLS''\n        or im.last_method_of_contraception = ''ORAL_PILLS'' then ''Oral pills''\n        when im.last_method_of_contraception = ''IUCD5'' then ''IUCD 5 years''\n        when im.last_method_of_contraception = ''IUCD10'' then ''IUCD 10 years''\n        when im.last_method_of_contraception = ''STERILIZATION'' then ''Sterilization''\n        when im.last_method_of_contraception = ''CONDOM'' then ''Condom''\n        when im.last_method_of_contraception = ''MLSTR'' then ''Male sterilization''\n        when im.last_method_of_contraception = ''CONTRA'' then ''Emergency Contraceptive Pills''\n        when im.last_method_of_contraception = ''INJECTABLES'' then ''Injectables''\n        when im.last_method_of_contraception = ''BARRIER_METHOD'' then ''Barrier Method''\n        when im.last_method_of_contraception = ''CHHAYA'' then ''Chhaya''\n        when im.last_method_of_contraception = ''ANTARA'' then ''Antara''\n        else im.last_method_of_contraception\n    end as last_method_of_contraception,\n    im.lm