enoughAlbendazole = false;\n                $formData$.inventoryDetails.albendazole_msg = \"You don''t have enough stock of albendazole\";\n            }\n        } else if (stock[\"medicine_name\"] === \"TT Vaccine\") {\n            $utilityData$.ttVaccineStock = stock[\"medicines_available\"];\n            $utilityData$.ttVaccineStockUsed = stock[\"used\"];\n        } else if (stock[\"medicine_name\"] === \"TT One Vaccine\") {\n            $utilityData$.ttOneStock = stock[\"medicines_available\"];\n            $utilityData$.ttOneStockUsed = stock[\"used\"];\n        } else if (stock[\"medicine_name\"] === \"TT Two Vaccine\") {\n            $utilityData$.ttTwoStock = stock[\"medicines_available\"];\n            $utilityData$.ttTwoStockUsed = stock[\"used\"];\n        } else if (stock[\"medicine_name\"] === \"FA Iron Tablets\") {\n            $utilityData$.faStock = stock[\"medicines_available\"];\n            $utilityData$.faStockUsed = stock[\"used\"];\n        } else if (stock[\"medicine_name\"] === \"IFA Tablets\") {\n            $utilityData$.ifaStock = stock[\"medicines_available\"];\n            $utilityData$.ifaStockUsed = stock[\"used\"];\n        } \n    })\n    $formData$.inventoryDetails.albendazoleUsed = $utilityData$.albendazoleStockUsed ? $utilityData$.albendazoleStockUsed : 0;\n    $formData$.inventoryDetails.faUsed = $utilityData$.faStockUsed ? $utilityData$.faStockUsed : 0;\n    $formData$.inventoryDetails.ifaUsed = $utilityData$.ifaStockUsed ? $utilityData$.ifaStockUsed : 0;\n});"},{"value":"setAlbendazoleInventory","code":"(function setAlbendazoleInventory() {\n\tif ($utilityData$.albendazoleInitial && !$formData$.ancDetails.albendazole_given)\n    {\n        $formData$.inventoryDetails.albendazoleUsed = $utilityData$.albendazoleStockUsed - 1;\n        $utilityData$.enoughAlbendazole = true;\n    }\n    else if (!$utilityData$.albendazoleInitial && $formData$.ancDetails.albendazole_given)\n    {\n        if ($utilityData$.albendazoleStock >= 1)\n        $formData$.inventoryDetails.albendazoleUsed = $utilityData$.albendazoleStockUsed + 1;\n        else {\n            $utilityData$.enoughAlbendazole = false\n        }\n    }\n    else\n    {\n        $formData$.inventoryDetails.albendazoleUsed = $utilityData$.albendazoleStockUsed;\n        $utilityData$.enoughAlbendazole = true;\n    }\n});"},{"value":"setFaInventory","code":"(function setFaInventory() {\n\t if (($utilityData$.faStock + $utilityData$.faInitial) >= $formData$.ancDetails.fa_tablets_given) {\n        $formData$.inventoryDetails.faUsed = ($utilityData$.faStockUsed - $utilityData$.faInitial) + $formData$.ancDetails.fa_tablets_given;\n        $utilityData$.enoughFa = true\n    }\n    else if (($utilityData$.faStock + $utilityData$.faInitial) < $formData$.ancDetails.fa_tablets_given){\n        $utilityData$.enoughFa = false\n        $formData$.ancDetails.fa_tablets_given = null\n        $formData$.inventoryDetails.fa_msg = \"You don''t have enough stock of Folic Acid tablets\";\n    }\n});"},{"value":"setIfaInventory","code":"(function setIfaInventory() {\n\tif (($utilityData$.ifaStock + $utilityData$.ifaInitial) >= $formData$.ancDetails.ifa_tablets_given) {\n        $formData$.inventoryDetails.ifaUsed = ($utilityData$.ifaStockUsed - $utilityData$.ifaInitial) + $formData$.ancDetails.ifa_tablets_given;\n        $utilityData$.enoughIfa = true\n    }\n    else if(($utilityData$.ifaStock + $utilityData$.ifaInitial) >= $formData$.ancDetails.ifa_tablets_given) {\n        $utilityData$.enoughIfa = false \n        $formData$.ancDetails.ifa_tablets_given = null\n        $formData$.inventoryDetails.ifa_msg = \"You don''t have enough stock of Iron and Folic Acid tablets\";\n    }\n});"}],"formQueries":[{"value":"Fetch member details","queryCode":"fetch_mem_details","params":"ancId,memberId","paramConfig":"{\"ancId\":\"$stateParams$.id\",\"memberId\":\"$formData$.ancDetails.member_id\"}","query":"with chw_det as (\nselect \nconcat_ws('' '',uu.first_name, uu.middle_name, uu.last_name, ''('',uu.contact_number,'')'') as chw_info,\nram.gestational_age_from_lmp,\nram.id\nfrom \num_user uu\ninner join um_user_location uul on uul.user_id = uu.id\ninner join location_hierchy_closer_det lhcd on lhcd.child_id = uul.loc_id \ninner join rch_anc_master ram on ram.location_id = lhcd.parent_id \nwhere ram.id = #ancId#\nand uu.role_id = (select id from um_role_master urm where name = ''Community Health Worker'')\norder by ram.id desc limit 1\n)\n,hof_det as (\nselect im.mobile_number\nfrom imt_member im\nwhere im.family_id = (select family_id from imt_member where id = #memberId#)\nand im.family_head\n)\n,chronic_diseases as (\nselect string_agg(lfvd.value,'','') as chronic_dis\nfrom listvalue_field_value_detail lfvd\ninner join imt_member_chronic_disease_rel rel on rel.chronic_disease_id = lfvd.id\nwhere rel.member_id = #memberId#\n)\n,previous_illness as (\nselect string_agg(lfvd.value,'','') as previous_illness\nfrom listvalue_field_value_detail lfvd\ninner join imt_member_current_disease_rel rel on rel.current_disease_id = lfvd.id\nwhere rel.member_id = #memberId#\n)\nselect im.unique_health_id,\nconcat_ws('' '',im.first_name,im.middle_name,im.last_name) as member_name,\ncast(age(im.dob) as text) as age,\nim.dob,\ncase when im.gender = ''F'' then ''Female'' else ''Male'' end as gender,\t\n(select value from listvalue_field_value_detail where id = im.marital_status) as marital_status,\n(select value from listvalue_field_value_detail where cast(id as text) = im.religion) as religion,\t\nim.lmp,\nim.edd,\nim.haemoglobin,\ncast(im.additional_info as json)->> ''hivTest'' as hiv_status,\nim.weight,\nim.blood_group,\nim.last_delivery_date,\nim.immunisation_given,\nim.current_para,\nim.current_gravida,\nim.anc_visit_dates,\nim.cur_preg_reg_date,\ncase when (im.cur_preg_reg_date - im.lmp) <= ''90 days'' then true\nelse false end as early_pregnancy,\ncase when im.is_high_risk_case = true then ''Yes'' \nwhen im.is_high_risk_case = false then ''No'' \n--else im.is_high_risk_case \nend as is_high_risk_case,\ncast(age(im.date_of_wedding, im.dob) as text) as age_at_wedding,\ncast(im.additional_info as json)->> ''lastServiceLongGate'' as last_service_date,\ncast(im.additional_info as json)->> ''havingBirthPlan'' as having_birth_plan,\nhof_det.mobile_number as family_phone_no,\nconcat_ws('' '',imf.house_number,imf.address1, imf.address2) as family_address,\nchw_det.chw_info,\nchw_det.gestational_age_from_lmp,\nchronic_diseases.chronic_dis,\nprevious_illness.previous_illness\nfrom imt_member im \ninner join imt_family imf on imf.family_id = im.family_id    \ninner join chw_det on true\ninner join hof_det on true \ninner join chronic_diseases on true\ninner join previous_illness on true\nwhere im.id = #memberId#;","response":"$infoData$.memberDetails","isResultArray":null,"successMessage":null,"failureMessage":"Member not found.","isResponseMandatory":true,"isField":null,"isPagination":false},{"value":"Fetch children info","queryCode":"fetch_children_details","params":"memberId","paramConfig":"{\"memberId\":\"$formData$.ancDetails.member_id\"}","query":"with children as (\nselect id, \nconcat_ws('' '',first_name, middle_name, last_name) as name,\ncast(age(dob) as text) as age, gender\nfrom imt_member\nwhere mother_id = #memberId#\norder by dob desc\n)\nselect \n(select count(id) from children) as children_count,\nname, age, gender from children limit 1;","response":"$infoData$.childrenDetails","isResultArray":null,"successMessage":null,"failureMessage":null,"isResponseMandatory":null,"isField":null,"isPagination":false},{"value":"Fetch ANC Details","queryCode":"fetch_anc_details","params":"ancId","paramConfig":"{\"ancId\":\"$stateParams$.id\"}","query":"select \nram.id as anc_id,\nram.member_id,\nram.service_date,\nram.member_status,\nram.no_of_preg,\nram.any_twin,\nram.any_premature_birth,\nim.mobile_number,\nram.transport_arranged,\nram.having_birth_plan,\nram.lmp,\nram.weight,\nram.blood_group,\nram.haemoglobin_count,\nram.systolic_bp,\nram.diastolic_bp,\nram.member_height,\nram.foetal_movement,\nram.foetal_height,\nram.foetal_heart_sound,\nram.foetal_position,\nram.ifa_tablets_given,\nram.fa_tablets_given,\ncast(im.additional_info as json)->>''hbsagTest'' as \"hbsagTest\",\nram.hbsag_test as hba1c_test,\nram.blood_sugar_test,\nram.sugar_test_before_food_val,\nram.sugar_test_after_food_val,\nram.urine_test_done,\nram.urine_albumin,\nram.urine_sugar,\ncast(im.additional_info as json)->>''vdrlTest'' as \"vdrlTest\",\nram.vdrl_test as syphilis_test,\ncast(im.additional_info as json)->>''hivTest'' as \"hivTestDone\",\nram.hiv_test as hiv_test_status,\nram.cd_four_level,\nram.viral_load,\nram.coinfections,\nram.is_hiv_counselling_done,\nram.is_art_started,\nram.is_partner_tested_positive_for_hiv,\ncast(im.additional_info as json)->>''sickleCellTest'' as \"sickleCellTestDone\",\nram.sickle_cell_test,\ncast(im.additional_info as json)->>''albendanzoleGiven'' as \"albendanzoleGiven\",\nram.albendazole_given,\nram.dangerous_sign_id,\nram.other_dangerous_sign,\nram.expected_delivery_place,\nram.family_planning_method,\n--oral pills/ injectable/ natural method/ barrier method/ implant/ sterilization/ iucd\nram.fp_sub_method,\n--alternative fp method with oral pills/ natural method/ barrier method\nram.fp_alternative_main_method ,\n--natural method/barrier method along oral pills(alternative sub method), oral pill/barrier method along natural method, oral pill/natural with barrier\nram.fp_alternative_sub_method,\nram.dead_flag,\nram.child_alive,\nram.death_date,\ncase when ram.other_death_place is not null then ''OTHER''\nelse ram.place_of_death end as place_of_death,\nram.other_death_place,\nram.death_infra_id,\n-- field_key = 2018\ncase when ram.other_death_reason is not null then ''OTHER'' \nelse ram.death_reason end as death_reason,\nram.other_death_reason,\nram.referral_done,\nram.referral_place,\ncast(ram.referral_for as int) as referral_for,\nram.referral_reason,\nram.is_iec_given\nfrom rch_anc_master ram \ninner join imt_member im on im.id = ram.member_id \nwhere ram.id = #ancId#\norder by ram.id desc limit 1","response":"$formData$.ancDetails","isResultArray":null,"successMessage":null,"failureMessage":null,"isResponseMandatory":null,"isField":null,"isPagination":false},{"value":"Fetch TT dates","queryCode":"fetch_tt_dates","params":"memberId","paramConfig":"{\"memberId\":\"$formData$.ancDetails.member_id\"}","query":"select \n(select given_on \nfrom rch_immunisation_master\nwhere member_id = #memberId#\nand visit_type = ''FHW_ANC''\nand immunisation_given = ''TT_BOOSTER'') as tt_booster_date,\n(select given_on \nfrom rch_immunisation_master\nwhere member_id = #memberId#\nand visit_type = ''FHW_ANC''\nand immunisation_given = ''TT1'') as tt1_date,\n(select given_on \nfrom rch_immunisation_master\nwhere member_id = #memberId#\nand visit_type = ''FHW_ANC''\nand immunisation_given = ''TT2'') as tt2_date","response":"$formData$.ttDates","isResultArray":null,"successMessage":null,"failureMessage":null,"isResponseMandatory":null,"isField":null,"isPagination":false},{"value":"Get user details","queryCode":"get_user_details","params":"userID","paramConfig":"{\"userID\":\"$loggedInUserId$\"}","query":"select uhi.health_infrastrucutre_id as user_hf, hid.name as user_hf_name,\n(select is_user_smag_trained\nfrom um_user where id = #userID# and state = ''ACTIVE'')as is_user_smag_trained\nfrom user_health_infrastructure uhi \ninner join health_infrastructure_details hid on hid.id = uhi.health_infrastrucutre_id  \nwhere uhi.user_id = #userID# and uhi.is_default = true\nand uhi.state = ''ACTIVE'' and hid.state = ''ACTIVE'';","response":"$utilityData$.userDetails","isResultArray":false,"successMessage":null,"failureMessage":null,"isResponseMandatory":false,"isField":false,"isPagination":false,"toVariable":false},{"value":"Get health infra","queryCode":"get_health_infra","params":"","paramConfig":"{}","query":"select  h.name , h.id,h.type as type from 