--

CREATE FUNCTION public.truncate_schema(_schema text) RETURNS void
    LANGUAGE plpgsql
    AS $$
declare
    selectrow record;
begin
    for selectrow in
        select 'TRUNCATE TABLE ' || quote_ident(_schema) || '.' ||quote_ident(t.table_name) || ' RESTART IDENTITY CASCADE;' as qry
        from (
            SELECT table_name
            FROM information_schema.tables
            WHERE table_type = 'BASE TABLE' 
                    AND table_schema = _schema 
                    AND table_name != 'flyway_schema_history' 
                    AND table_name != 'schema_version'
                    AND table_name != 'oauth_client_details'
                    AND table_name != 'system_configuration'
                    AND table_name != 'query_master'
                    AND table_name != 'soh_element_configuration'
                    AND table_name != 'soh_chart_configuration'
                    AND table_name != 'listvalue_field_master'
                    AND table_name != 'listvalue_field_role'
                    AND table_name != 'listvalue_field_value_detail'
                    AND table_name != 'listvalue_form_master'
                    AND table_name != 'menu_config'
                    AND table_name != 'menu_group'
                    AND table_name != 'location_type_master'
                    AND table_name != 'location_state_master'
        )t
        loop
            execute selectrow.qry;
        end loop;
end;
$$;


